import React from "react";
import "../css/sipCalculator.css";
import InputRangeLeft, { InputRangeRight } from "../Components/formInput";
import NavBar from "../Components/navbar";
import DoughnutChart from "../Components/doughnut";
import Footer from "../Components/footer";
import BlackStrip from "../Components/blackStrip";
import TableData from "../Components/tableData";
import FDInfo from "./fdCalInfo";

export default function FDCal() {
  const [stats, setStats] = React.useState({
    totalInvestment: 5000,
    interestRate: 1,
    timePeriod: 1,
  });
  //   const rate = stats.interestRate / 100;
  //   const totalInvestment = stats.totalInvestment * stats.timePeriod * 12;
  var returnAmt =
    (stats.totalInvestment * stats.interestRate * stats.timePeriod) / 100;
  // data for doughnut chart
  const data = {
    labels: ["Total Investment", "Total Returns"],
    datasets: [
      {
        label: "FD Calculation",
        data: [stats.totalInvestment, returnAmt],
        borderColor: ["black"],
        backgroundColor: ["#91BCE5", "#0E2A40"],
        pointBackgroundColor: "red",
      },
    ],
  };
  // options for doughnut chart
  const options = {
    plugins: {
      title: {
        display: true,
        text: "FD Calculator",
        color: "black",
        font: {
          size: 34,
        },
        padding: {
          top: 30,
          bottom: 30,
        },
        responsive: true,
        animation: {
          animateScale: true,
        },
      },
    },
  };

  function handleChange(event) {
    const { name, value } = event.target;
    return setStats((prevValue) => {
      return {
        ...prevValue,
        [name]: value,
      };
    });
  }
  return (
    <div>
      <BlackStrip bgcolor="#1e1b1b" height="2.2em" />
      <NavBar />

      <div class="sipContainer">
        <div class="heading">
          <h4 class="calHeading">Fixed Deposit Calculator</h4>
          <h6 class="calTagline">Find your maturity amount easily !!</h6>
        </div>
        <div class="row stats">
          <div class="col-sm-12 col-md-5 align-self-center">
            <InputRangeLeft
              label="Monthly Investment"
              id="customRange1"
              name="totalInvestment"
              unit="₹"
              rangeOutput={stats.totalInvestment}
              onChange={handleChange}
              min={5000}
              max={10000000}
              step={30000}
            />

            <InputRangeRight
              label="Expected return rate (p.a.)"
              id="customRange2"
              name="interestRate"
              unit="%"
              rangeOutput={stats.interestRate}
              onChange={handleChange}
              min={1}
              max={15}
              step={0.5}
            />

            <InputRangeRight
              label="Time Period"
              id="customRange3"
              name="timePeriod"
              unit="Yr"
              rangeOutput={stats.timePeriod}
              onChange={handleChange}
              min={1}
              max={30}
              step={1}
            />
            <br />
            <TableData
              title="Investment Amount"
              unit="₹"
              data={stats.totalInvestment}
            />
            <TableData title="Est. Returns" unit="₹" data={returnAmt} />
            <TableData
              title="Total Value"
              unit="₹"
              data={Number(stats.totalInvestment)+returnAmt}
            />
          </div>

          {/* for chart--> */}
          <div class="col-sm-12 col-md-6" id="pie">
            <DoughnutChart data={data} options={options} />
          </div>
        </div>
      </div>
      <FDInfo />
      <Footer />
    </div>
  );
}
