import React, { useState } from "react";
import "../css/sipCalculator.css";
import NavBar from "../Components/navbar";
import BlackStrip from "../Components/blackStrip";
import { InputNumberLeft } from "../Components/formInput";
import { incomeDetails } from "../dataset/incomeSection2";
import { deductions } from "../dataset/incomeSection3";
import Footer from "../Components/footer";


export default function IncomeTaxCal(){
    const [stats,setStats]=useState({
        // financialYear:1,
        // ageGroup:1,
        incomeSalary:0,
        exemptAllowance:0,
        interestIncome:0,
        interestHomeLoanSelfOccupied:0,
        incomeRental:0,
        interestHomeLoanLetOut:0,
        incomeDigitalAssets:0,
        incomeOthers:0,
        deduc80C:0,
        deduc80TTB:0,
        deduc80D:0,
        deduc80G:0,
        deduc80E:0,
        deduc80EEA:0,
        deduc80CCD:0,
    });
    function handleInput(event) {
        const { name, value } = event.target;
        return setStats((prevValue) => {
          return {
            ...prevValue,
            [name]: value,
          };
        });
    }


    return(
        <div>
            <BlackStrip bgcolor="#1e1b1b" height="2.2em" />
            <NavBar />
            <div className="sipContainer">
                <div class="heading">
                    <h4 class="calHeading">Income Tax Calculator</h4>
                    <h6 class="calTagline">Find how much tax I need to pay</h6>
                </div>
                <br />
                <br />

                {/* Section 1 */}
                <i>See how the latest budget impacts your tax calculation. Updated as per latest budget on 1 February, 2023.</i>
                <div className="formQuestion">Which Financial Year do you want to calculate taxes for?</div>
                <div class="form-check">
                    <input class="form-check-input" type="radio" name="financialYear" id="flexRadioDefault1" value={stats.deduc80C}/>
                    <label class="formOption form-check-label" for="flexRadioDefault1">
                     FY 2023-2024 <i>Latest Budget</i> (Return to be filed between 1st April 2024 - 31st March 2025)
                    </label>
                </div>
                <div class="form-check">
                    <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" />
                    <label class="formOption form-check-label" for="flexRadioDefault1">
                    FY 2022-2023 (Return to be filed between 1st April 2023 - 31st March 2024)
                    </label>
                </div>





                {/* Section 2 */}
                {incomeDetails.map((data)=>(
                    <InputNumberLeft
                    label={data.label}
                    id={data.name}
                    name={data.name}
                    unit="₹"
                    onInput={handleInput}
                    min="0"
                    // value={stats.incomeSalary}
                  />
                ))}
                {/* {console.log(incomeDetails[0].id)} */}


                {/* Section 3 */}

                {deductions.map((data)=>(
                    <InputNumberLeft
                    label={data.label}
                    id={data.name}
                    name={data.name}
                    unit="₹"
                    onInput={handleInput}
                    min="0"
                    // value={stats.incomeSalary}
                  />
                ))}


            </div>
            <br /><br /><br /><br />
            <Footer />
        </div>
    );


}
